const { join } = require('path');

exports.deploy = (_model, _db, options) => {
  // locate deployer relative to sap/cds
  const cdsHome = require('../../lib/cds').home;
  const deployer = require(join(cdsHome, 'bin/deploy/to-hana'));

  const bind = !options['store-credentials'] ? require('../bind') : undefined;
  return deployer.deploy(_model, _db, options, bind);
}
